// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

/**
 * @title ICreditRegistry
 * @notice Interface for the CreditRegistry contract - central registry for credit events and scores
 * @dev Scores are stored privately - only the user can read their own score via msg.sender
 */
interface ICreditRegistry {
    // ============ Enums ============

    enum ActionType {
        SWAP,
        LEND,
        REPAY,
        STAKE,
        TRANSFER,
        PROVIDE_LIQUIDITY
    }

    // ============ Structs ============

    struct CreditEvent {
        address user;
        ActionType actionType;
        uint256 amount;
        uint256 timestamp;
        uint256 pointsEarned;
    }

    // ============ Events ============

    event CreditEventRecorded(
        address indexed user,
        ActionType indexed actionType,
        uint256 amount,
        uint256 pointsEarned,
        uint256 timestamp
    );

    event ActionWeightUpdated(ActionType indexed actionType, uint256 newWeight);

    event InterceptorUpdated(address indexed oldInterceptor, address indexed newInterceptor);

    event ScoreCommitmentUpdated(address indexed user, bytes32 commitment);

    // ============ Core Functions ============

    /**
     * @notice Registers a new credit event for a user
     * @param user The user address
     * @param actionType The type of action performed
     * @param amount The amount involved in the action
     * @dev Only callable by the authorized interceptor
     */
    function registerCreditEvent(
        address user,
        ActionType actionType,
        uint256 amount
    ) external;

    // ============ View Functions (Private Access) ============

    /**
     * @notice Get the caller's own credit score
     * @return The credit score of msg.sender
     * @dev Only returns score for msg.sender - privacy by design
     */
    function getMyScore() external view returns (uint256);

    /**
     * @notice Get the caller's own credit history
     * @return Array of credit events for msg.sender
     * @dev Only returns history for msg.sender - privacy by design
     */
    function getMyCreditHistory() external view returns (CreditEvent[] memory);

    // ============ View Functions (Public Access) ============

    /**
     * @notice Get the score commitment for any user (hash, not actual score)
     * @param user The user address
     * @return The keccak256 hash of (score, salt)
     */
    function getScoreCommitment(address user) external view returns (bytes32);

    /**
     * @notice Get the number of credit events for a user
     * @param user The user address
     * @return The count of credit events
     */
    function getCreditEventCount(address user) external view returns (uint256);

    /**
     * @notice Get the merkle root of all credit events
     * @return The current registry merkle root
     */
    function getRegistryMerkleRoot() external view returns (bytes32);

    /**
     * @notice Get the weight for a specific action type
     * @param actionType The action type
     * @return The weight multiplier for the action
     */
    function getActionWeight(ActionType actionType) external view returns (uint256);

    /**
     * @notice Get total number of credit events in the registry
     * @return Total count of all credit events
     */
    function getTotalCreditEvents() external view returns (uint256);

    // ============ Admin Functions ============

    /**
     * @notice Set the weight for an action type
     * @param actionType The action type to configure
     * @param weight The weight multiplier
     * @dev Only callable by owner
     */
    function setActionWeight(ActionType actionType, uint256 weight) external;

    /**
     * @notice Set the authorized interceptor address
     * @param interceptor The new interceptor address
     * @dev Only callable by owner
     */
    function setInterceptor(address interceptor) external;

    /**
     * @notice Initialize the contract (for proxy pattern)
     * @param owner The owner address
     * @param defaultWeights Array of default weights for each action type
     */
    function initialize(address owner, uint256[] calldata defaultWeights) external;
}

project/contracts/interfaces/ICreditVaul.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

/**
 * @title ICreditVault
 * @notice Interface for the CreditVault - undercollateralized lending vault
 * @dev Accepts GhostScore ZK proofs for loan eligibility
 */
interface ICreditVault {
    // ============ Enums ============

    enum LoanStatus {
        ACTIVE,
        REPAID,
        DEFAULTED
    }

    // ============ Structs ============

    struct Loan {
        address borrower;
        uint256 amount;
        uint256 interestRate;     // in basis points (100 = 1%)
        uint256 startTime;
        uint256 duration;         // in seconds
        uint256 repaid;
        uint256 collateral;
        LoanStatus status;
    }

    struct LoanTier {
        uint256 scoreThreshold;   // minimum score required
        uint256 maxAmount;        // maximum loan amount
        uint256 interestBps;      // interest rate in basis points
        uint256 durationDays;     // loan duration in days
        uint256 collateralBps;    // required collateral in basis points (10000 = 100%)
    }

    // ============ Events ============

    event LiquidityDeposited(address indexed lender, uint256 amount);
    event LiquidityWithdrawn(address indexed lender, uint256 amount);

    event LoanIssued(
        uint256 indexed loanId,
        address indexed borrower,
        uint256 amount,
        uint256 interestRate,
        uint256 collateralRequired,
        uint256 duration
    );

    event LoanRepaid(
        uint256 indexed loanId,
        address indexed borrower,
        uint256 amount,
        uint256 remaining
    );

    event LoanDefaulted(
        uint256 indexed loanId,
        address indexed borrower,
        uint256 collateralSeized
    );

    event TierUpdated(uint256 indexed scoreThreshold, LoanTier tier);
    event GhostScoreVerifierUpdated(address indexed oldVerifier, address indexed newVerifier);

    // ============ Lender Functions ============

    /**
     * @notice Deposit liquidity into the vault
     * @param amount The amount to deposit
     */
    function depositLiquidity(uint256 amount) external;

    /**
     * @notice Withdraw liquidity from the vault
     * @param amount The amount to withdraw
     */
    function withdrawLiquidity(uint256 amount) external;

    // ============ Borrower Functions ============

    /**
     * @notice Request a loan using GhostScore proof
     * @param ghostProof The ZK proof of credit score
     * @param publicInputs Public inputs for proof verification
     * @param amount The requested loan amount
     * @return loanId The ID of the created loan
     */
    function requestLoan(
        bytes calldata ghostProof,
        bytes32[] calldata publicInputs,
        uint256 amount
    ) external payable returns (uint256 loanId);

    /**
     * @notice Request a loan using plain score (for demo without ZK)
     * @param amount The requested loan amount
     * @return loanId The ID of the created loan
     * @dev This is a simplified version for hackathon demo
     */
    function requestLoanSimple(uint256 amount) external payable returns (uint256 loanId);

    /**
     * @notice Repay a loan (partial or full)
     * @param loanId The loan ID to repay
     * @param amount The repayment amount
     */
    function repayLoan(uint256 loanId, uint256 amount) external;

    /**
     * @notice Liquidate a defaulted loan
     * @param loanId The loan ID to liquidate
     */
    function liquidate(uint256 loanId) external;

    // ============ View Functions ============

    /**
     * @notice Get loan details
     * @param loanId The loan ID
     * @return The loan struct
     */
    function getLoan(uint256 loanId) external view returns (Loan memory);

    /**
     * @notice Get loan tier for a score
     * @param score The credit score
     * @return The applicable loan tier
     */
    function getLoanTierForScore(uint256 score) external view returns (LoanTier memory);

    /**
     * @notice Get available liquidity in the vault
     * @return The available amount
     */
    function getAvailableLiquidity() external view returns (uint256);

    /**
     * @notice Get total deposited in the vault
     * @return Total deposited amount
     */
    function getTotalDeposited() external view returns (uint256);

    /**
     * @notice Get total borrowed from the vault
     * @return Total borrowed amount
     */
    function getTotalBorrowed() external view returns (uint256);

    /**
     * @notice Get lender's deposited amount
     * @param lender The lender address
     * @return Deposited amount
     */
    function getLenderDeposit(address lender) external view returns (uint256);

    /**
     * @notice Get borrower's active loan IDs
     * @param borrower The borrower address
     * @return Array of loan IDs
     */
    function getBorrowerLoans(address borrower) external view returns (uint256[] memory);

    // ============ Admin Functions ============

    /**
     * @notice Set a loan tier
     * @param tier The tier configuration
     */
    function setTier(LoanTier calldata tier) external;

    /**
     * @notice Set the GhostScore verifier address
     * @param verifier The verifier address
     */
    function setGhostScoreVerifier(address verifier) external;

    /**
     * @notice Set the credit interceptor for repayment events
     * @param interceptor The interceptor address
     */
    function setInterceptor(address interceptor) external;

    /**
     * @notice Initialize the contract (for proxy pattern)
     * @param owner The owner address
     * @param lendingToken The token used for lending
     * @param registry The credit registry address
     */
    function initialize(
        address owner,
        address lendingToken,
        address registry
    ) external;
}

project/contracts/interfaces/IGhostScore.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

/**
 * @title IGhostScoreVerifier
 * @notice Interface for the GhostScore ZK proof verifier
 * @dev Wraps Noir-generated UltraVerifier and stores attestations
 */
interface IGhostScoreVerifier {
    // ============ Structs ============

    struct GhostScoreAttestation {
        uint256 scoreThreshold;    // The minimum score proven
        uint256 timestamp;         // When the attestation was created
        bool valid;                // Whether attestation is still valid
    }

    // ============ Events ============

    event GhostScoreVerified(
        bytes32 indexed userCommitment,
        uint256 scoreThreshold,
        uint256 timestamp
    );

    event AttestationInvalidated(
        address indexed user,
        uint256 indexed attestationIndex
    );

    event VerifierUpdated(address indexed oldVerifier, address indexed newVerifier);

    // ============ Functions ============

    /**
     * @notice Verify a GhostScore proof and create attestation
     * @param proof The ZK proof bytes
     * @param publicInputs Public inputs [scoreThreshold, userCommitment]
     * @return success Whether verification succeeded
     */
    function verifyAndAttest(
        bytes calldata proof,
        bytes32[] calldata publicInputs
    ) external returns (bool success);

    /**
     * @notice Get an attestation for a user
     * @param user The user address
     * @param index The attestation index
     * @return The attestation struct
     */
    function getAttestation(
        address user,
        uint256 index
    ) external view returns (GhostScoreAttestation memory);

    /**
     * @notice Check if user has valid attestation above threshold
     * @param user The user address
     * @param minThreshold The minimum score threshold
     * @return Whether user has valid attestation
     */
    function hasValidAttestation(
        address user,
        uint256 minThreshold
    ) external view returns (bool);

    /**
     * @notice Get the number of attestations for a user
     * @param user The user address
     * @return The attestation count
     */
    function getAttestationCount(address user) external view returns (uint256);

    /**
     * @notice Invalidate an attestation (user can self-invalidate)
     * @param index The attestation index to invalidate
     */
    function invalidateAttestation(uint256 index) external;

    /**
     * @notice Set the UltraVerifier contract address
     * @param verifier The verifier address
     */
    function setVerifier(address verifier) external;

    /**
     * @notice Initialize the contract (for proxy pattern)
     * @param owner The owner address
     * @param verifier The UltraVerifier address (optional, can be zero for mock mode)
     */
    function initialize(address owner, address verifier) external;
}
